import React, { useEffect, useState } from "react";
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  SafeAreaView,
  TextInput,
  Button,
  Keyboard,
  TouchableWithoutFeedback,
} from "react-native";
import {} from "react-native";

import AsyncStorage from "@react-native-async-storage/async-storage";
import * as SMS from "expo-sms";
import * as Location from "expo-location";

export default function SmartLocationPage({ navigation, route }) {
  const [value, onChangeValue] = useState(undefined);
  const [actualValue, setActualValue] = useState();
  const [location, setLocation] = useState(null);
  const [errorMsg, setErrorMsg] = useState(null);
  const [contact, setContact] = useState("");

  useEffect(() => {
    if (route.params?.post) {
      console.log(route.params?.post);
      setContact(route.params?.post);
    }
  }, [route.params?.post]);

  _storeData = async () => {
    try {
      await AsyncStorage.setItem("emergencyContact", value);
    } catch (error) {
      console.log("error");
    }
  };

  _retrieveData = async () => {
    try {
      const value = await AsyncStorage.getItem("emergencyContact");
      if (value !== null) {
        setActualValue(value);
      }
    } catch (error) {
      console.log("error");
    }
  };
  useEffect(() => {
    _retrieveData();
  }, []);

  useEffect(() => {
    (async () => {
      if (Platform.OS === "android" && !Constants.isDevice) {
        setErrorMsg(
          "Oops, this will not work on Snack in an Android emulator. Try it on your device!"
        );
        return;
      }
      let { status } = await Location.requestPermissionsAsync();
      if (status !== "granted") {
        setErrorMsg("Permission to access location was denied");
        return;
      }

      let location = await Location.getCurrentPositionAsync({});
      setLocation(location);
    })();
  }, []);

  const sendSMS = () => SMS.sendSMSAsync(actualValue, JSON.stringify(location));
  console.log(contact);
  return (
    <TouchableWithoutFeedback
      onPress={Keyboard.dismiss}
      style={styles.container}
    >
      <View>
        <SafeAreaView style={styles.container}>
          <View style={styles.inputContainer}>
            <Text style={styles.titleText}>Smart Location</Text>
            <Text>
              Smart Location functionality will automatically send the current
              position to the emergency contact.
            </Text>
            <Text style={styles.titleText}>Emergency Contact Number</Text>
            <TextInput
              style={styles.textInput}
              keyboardType="numeric"
              onChangeText={(text) => onChangeValue(text)}
              value={value}
              defaultValue={actualValue}
            />
            <TouchableOpacity
              onPress={() => navigation.navigate("SearchableContacts")}
            >
              <Text style={styles.plainButton}>Search from Contacts</Text>
            </TouchableOpacity>
            {value !== undefined && (
              <TouchableOpacity
                style={styles.saveButton}
                title="Send Location"
                onPress={() => {
                  _storeData("emergencyContact", value);
                  _retrieveData();
                  Keyboard.dismiss();
                  onChangeValue(undefined);
                }}
              >
                <Text style={styles.buttonText}>Save</Text>
              </TouchableOpacity>
            )}

            <TouchableOpacity
              style={styles.sendLocation}
              title="Send Location"
              onPress={() => {
                sendSMS();
              }}
            >
              <Text style={styles.buttonText}>Send Location</Text>
            </TouchableOpacity>
          </View>
        </SafeAreaView>
      </View>
    </TouchableWithoutFeedback>
  );
}

const styles = StyleSheet.create({
  container: {
    height: "100%",
    backgroundColor: "#ffffff",
    paddingTop: 25,
  },
  titleText: {
    fontSize: 20,
    fontWeight: "bold",
    marginTop: 16,
    marginBottom: 8,
  },
  contactsList: {
    height: "100%",
    width: "100%",
  },
  textInput: {
    height: 45,
    borderRadius: 8,
    borderColor: "gray",
    borderWidth: 0.5,
    padding: 4,
    fontSize: 16,
  },
  inputContainer: {
    margin: 16,
  },
  saveButton: {
    backgroundColor: "#ff9933",
    alignItems: "center",
    padding: 15,
    marginVertical: 15,
    borderRadius: 8,
  },
  plainButton: {
    color: "#009999",
    fontSize: 20,
    paddingVertical: 16,
  },
  sendLocation: {
    backgroundColor: "#009999",
    display: "flex",
    justifyContent: "flex-end",
    alignItems: "center",
    padding: 15,
    marginVertical: 15,
    borderRadius: 8,
  },
  buttonText: {
    color: "white",
    fontWeight: "bold",
  },
});
