import React, { useEffect, useState } from "react";
import {
  View,
  Text,
  StyleSheet,
  SafeAreaView,
  TextInput,
  FlatList,
} from "react-native";
import {} from "react-native";
import GlobalStyles from "../../GlobalStyles";

import * as Contacts from "expo-contacts";
import { TouchableOpacity } from "react-native-gesture-handler";

export default function SearchableContacts(props) {
  const [contacts, setContacts] = useState([]);
  const [inMemoryContacts, setInMemoryContacts] = useState([]);

  useEffect(() => {
    (async () => {
      const { status } = await Contacts.requestPermissionsAsync();
      if (status === "granted") {
        const { data } = await Contacts.getContactsAsync({
          fields: [Contacts.Fields.PhoneNumbers, Contacts.Fields.Emails],
        });
        setContacts(data);
        setInMemoryContacts(data);
      }
    })();
  }, []);

  renderItem = ({ item }) => (
    <View style={{ minHeight: 70, padding: 5 }}>
      <Text style={{ color: "#bada55", fontWeight: "bold", fontSize: 26 }}>
        {item.firstName + " "}
        {item.lastName}
      </Text>
      <Text style={{ color: "white", fontWeight: "bold" }}>
        {item.phoneNumbers[0].digits}
      </Text>
    </View>
  );

  searchContacts = (value) => {
    const lowerCaseValue = value.toLowerCase();
    const filteredContacts = inMemoryContacts.filter(
      (contact) =>
        contact.firstName?.indexOf(value) > -1 ||
        contact.firstName?.indexOf(lowerCaseValue) > -1
    );
    setContacts(filteredContacts);
  };

  return (
    <SafeAreaView style={GlobalStyles.adroidSafeArea}>
      <TextInput
        placeholder="Search"
        placeholderTextColor="#ffffff"
        style={styles.searchBar}
        onChangeText={(value) => searchContacts(value)}
      />

      <View style={{ flex: 1, backgroundColor: "#2f363c" }}>
        <View
          style={{
            ...StyleSheet.absoluteFill,
            alignItems: "center",
            justifyContent: "center",
          }}
        ></View>

        <FlatList
          data={contacts}
          renderItem={renderItem}
          keyExtractor={(item, index) => index.toString()}
          ListEmptyComponent={() => (
            <View
              style={{
                flex: 1,
                alignItems: "center",
                justifyContent: "center",
                marginTop: 50,
              }}
            >
              <Text style={{ color: "#bad555" }}>No Contacts Found</Text>
            </View>
          )}
        />
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },

  searchBar: {
    height: 50,
    width: "90%",
    fontSize: 36,
    padding: 10,
    color: "white",
    borderBottomWidth: 1,
    borderBottomColor: "#7d90a0",
  },
});
