import React, { useEffect, useState } from "react";
import {
  View,
  Text,
  StyleSheet,
  SafeAreaView,
  TextInput,
  FlatList,
  TouchableOpacity,
} from "react-native";
import {} from "react-native";

import * as Contacts from "expo-contacts";

export default function SearchableContacts({ navigation, route }) {
  const [contacts, setContacts] = useState([]);
  const [contact, setContact] = useState("");
  const [inMemoryContacts, setInMemoryContacts] = useState([]);

  useEffect(() => {
    (async () => {
      const { status } = await Contacts.requestPermissionsAsync();
      if (status === "granted") {
        const { data } = await Contacts.getContactsAsync({
          fields: [Contacts.Fields.PhoneNumbers, Contacts.Fields.Emails],
        });
        setContacts(data);
        setInMemoryContacts(data);
      }
    })();
  }, []);

  renderItem = ({ item }) => (
    <View style={styles.row}>
      <View style={styles.topRow}>
        <View style={styles.textContainer}>
          <Text style={{ color: "#009999", fontWeight: "bold", fontSize: 26 }}>
            {item.firstName + " "}
            {item.lastName}
          </Text>
        </View>
        <View style={styles.buttonContainer}>
          <TouchableOpacity
            style={styles.selectButton}
            title="Send Location"
            onPress={() => {
              setContact(
                (item.phoneNumbers && item.phoneNumbers[0].digits) ||
                  (item.phoneNumbers && item.phoneNumbers[0].number)
              );
              navigation.navigate("SmartLocationPage", { post: contact });
            }}
          >
            <Text style={styles.buttonText}>Select</Text>
          </TouchableOpacity>
        </View>
      </View>
      <View style={styles.bottomRow}>
        <Text style={{ color: "black", fontWeight: "bold" }}>
          {item.phoneNumbers && item.phoneNumbers[0].digits}
          {item.phoneNumbers && item.phoneNumbers[0].number}
        </Text>
      </View>
    </View>
  );

  searchContacts = (value) => {
    const filteredContacts = inMemoryContacts.filter(
      (contact) =>
        contact.firstName?.toLowerCase().indexOf(value) > -1 ||
        contact.firstName?.indexOf(value) > -1
    );
    setContacts(filteredContacts);
  };
  return (
    <SafeAreaView style={styles.container}>
      <TextInput
        placeholder="Search"
        placeholderTextColor="black"
        style={styles.searchBar}
        onChangeText={(value) => searchContacts(value)}
      />

      <View>
        <FlatList
          data={contacts}
          renderItem={renderItem}
          keyExtractor={(item, index) => index.toString()}
        />
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#fff",
  },
  topRow: {
    display: "flex",
    flexDirection: "row",
    alignContent: "center",
    width: "100%",
  },
  row: {
    minHeight: 70,
    padding: 5,
    width: "100%",
  },
  textContainer: {
    width: "80%",
  },
  buttonContainer: {
    display: "flex",
    justifyContent: "flex-end",
  },
  selectButton: {
    backgroundColor: "#009999",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    paddingHorizontal: 15,
  },
  buttonText: {
    color: "white",
    fontWeight: "bold",
  },
  searchBar: {
    height: 50,
    fontSize: 20,
    paddingHorizontal: 8,
    color: "black",
    borderBottomWidth: 1,
    borderBottomColor: "#7d90a0",
  },
});
